import{_ as a}from"./index-BfWYZSqk.js";import{n as d}from"./index-BhiU7SED.js";const o={components:{LayoutEditor:a},data(){return{content:'<p><span id="alttext-container"><img id="alttext-image" width="16" height="16" align="left"><span id="alttext"></span></span></p><p>TinyMCE是一款易用、且功能强大的所见即所得的富文本编辑器。同类程序有：</p><table style="border-collapse: collapse;" border="1" data-mce-style="border-collapse: collapse;"><tbody><tr style="height: 21px;" data-mce-style="height: 21px;"><td style="width: 20%; height: 21px;" data-mce-style="width: 20%; height: 21px;">UEditor</td><td style="width: 20%; height: 21px;" data-mce-style="width: 20%; height: 21px;">Kindeditor</td><td style="width: 20%; height: 21px;" data-mce-style="width: 20%; height: 21px;">Simditor</td></tr><tr style="height: 21px;" data-mce-style="height: 21px;"><td style="width: 20%; height: 21px;" data-mce-style="width: 20%; height: 21px;">CKEditor</td><td style="width: 20%; height: 21px;" data-mce-style="width: 20%; height: 21px;">wangEditor</td><td style="width: 20%; height: 21px;" data-mce-style="width: 20%; height: 21px;">Suneditor</td></tr><tr style="height: 21px;" data-mce-style="height: 21px;"><td style="width: 20%; height: 21px;" data-mce-style="width: 20%; height: 21px;">froala</td><td style="width: 20%; height: 21px;" data-mce-style="width: 20%; height: 21px;">等等</td><td style="width: 20%; height: 21px;" data-mce-style="width: 20%; height: 21px;"><br></td></tr></tbody></table><h4>关于此中文文档（手册）</h4><p style="text-indent: 2em;" data-mce-style="text-indent: 2em;">该文档是<a href="http://tinymce.ax-z.cn/demos/" data-mce-href="./">TinyMCE的中文使用手册、进阶用法的说明书</a>，包含入门用法、插件使用、配置参数使用方法、内置方法等。<span style="color: #f30;" data-mce-style="color: #f30;">它并非100%照搬英文文档进行翻译</span>，我会对很多自认为不完善的地方进行删改或加入新的内容，使之比原文档更易于学习。</p><p>弄这个中文手册的初衷：从前一直使用kindeditor，但苦于官方已N久没有更新，不得不到处寻找合适自己的编辑器，用过很多之后，最终感觉还是TinyMCE比较顺手，主要是它能简能繁，适用非常全面。</p><p>老外的东西是不错的，就是没有中文手册文档，拦住好多像我一样英语比较差的国人，限制了它的知名度，希望这份中文文档能帮助TinyMCE走进中国。</p><p>我深知自己的英语水平比较烂，如有任何翻译错误，还请邮件告诉我：Lcard@qq.com</p><p>PS：网友群：689501894，吐槽群：818515594</p><p>—— 译者兼该文档制作人：莫若卿</p><p>—— 2019.3</p>',config:{toolbar:"undo redo searchreplace | cut copy paste pastetext | blocks fontsize | removeformat forecolor backcolor bold italic underline strikethrough | lineheight indent2em outdent indent align | numlist bullist table | link  image-library video-library importword | charmap emoticons insertdatetime | preview"}}},methods:{onChange(i){console.log(i.level.content)}}};var n=function(){var t=this,e=t._self._c;return e(a,{attrs:{content:t.content,config:t.config},on:{Change:t.onChange},scopedSlots:t._u([{key:"header",fn:function(){return[e("div",{staticClass:"flex justify-between p-8 border-b-1 border-b-solid border-b-gray-200"},[e("div",[e("el-input",{staticClass:"w-300",attrs:{placeholder:"请输入文章标题",maxlength:"100","show-word-limit":""}})],1),e("div",[e("el-button",{attrs:{type:"primary"}},[t._v("保存草稿")]),e("el-button",{on:{click:function(h){return t.$router.back()}}},[t._v("取消")]),e("el-button",[t._v("发表")])],1)])]},proxy:!0}])})},l=[],r=d(o,n,l,!1,null,null);const c=r.exports;export{c as default};
